#include "../include/ft_ssl.h"
#include "../include/ssl_test.h"

#define STRING1024	""
#define STRING1023	""
#define STRING513	""
#define STRING512	""
#define STRING511	""
#define STRING500	""
#define STRING448	""
#define STRING447	""

void test_string_to_block(char *str) {
	char *allocated_str = ft_strdup(str);
	t_list *lst = string_to_binary_block_list(allocated_str);
	t_list *tmp = lst;
	ft_printf_fd(1, RED"Test string len: %u -> "RESET, ft_strlen(str));
	ft_printf_fd(1, PINK"List size: %d\n"RESET""YELLOW"Data:\n"RESET, ft_lstsize(lst));
	while (tmp) {
		char *tmp_str = (char *)tmp->content;
		ft_printf_fd(1, "%s -> len %u\n", tmp_str, ft_strlen(tmp_str));
		tmp = tmp->next;
	}
	free(allocated_str);
	ft_lstclear(&lst, &free);

}

void test_s2bblist() {
	test_string_to_block(STRING447);
	test_string_to_block(STRING448);
	test_string_to_block(STRING500);
	test_string_to_block(STRING511);
	test_string_to_block(STRING512);
	test_string_to_block(STRING513);
	test_string_to_block(STRING1023);
	test_string_to_block(STRING1024);
}

int main() {
	run_test();
	test_s2bblist();
	return (0);
}